<?phpprint("<b>__tostring没啥实际意义，用print_r就可以替代<br/>同样，__clone也没啥意义，可以再new一个嘛<br/>__autoload()也没啥意义，程序员必须清楚的知道并且显示指明需要使用到外部类<br/></b>");class CTest{	private $Name;	private $Age;		public function __construct($Name,$Age){		$this->Name=$Name;		$this->Age=$Age;	}	public function Mod($Name,$Age){		$this->Name=$Name;		$this->Age=$Age;	}	function __call($n,$v){		print("调用不存在的方法:".$n."<br/>");		print("实参为:".print_r($v)."<br/>");	}}$OTest=new CTest("YangJun",24);print_r($OTest);print("<br/>");$OTest->NotExist("Method","Para");$OTestNew=$OTest;$OTestNew->Mod("YY",23);print_r($OTest);$OTest=new CTest("YangJun",24);$OTestNew=clone $OTest;$OTestNew->Mod("YY",25);print_r($OTest);include("TestClass.php");$OOT=new CTestClass("YangJun",24);print("<br/>");$OOT->DisInfo();?>